function Firestore() {
	const [data] = useCollection(
		query(collection(db, "/market/catalog/indoor_plants"))
	);

	const sendTask = async (data) => {
		await addDoc(collection(db, "catalog"), data);
	};

	const addDocument = () => {
		catalogTest.map((item) => {
			sendTask(item);
		});
	};
	const deleteTask = async (id) => {
		if (id != "NODELETE") {
			const docRef = doc(db, "catalog", id);
			try {
				await deleteDoc(docRef);
			} catch (error) {
				console.error("Error:", error);
			}
		}
	};

	// const deleteAllTasks = () => {
	// 	tasks.map((item) => {
	// 		deleteTask(item.id);
	// 	});
	// };
	return <Button onClick={addDocument}>sendCollection</Button>;
}
export default Firestore;
